  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'ww-i18n',
	title: 'Translation & Internationalization',
	sidebar_label: 'Translation & Internationalization'
};
export const metadata = {
  "id": "ww-i18n",
  "title": "Translation & Internationalization",
  "description": "This guide assumes you have followed the steps of both installing and setting up WordWeaver. It also assumes some familiarity with JSON, basic directory structure of your WordWeaver, Command Line, and NPM)",
  "source": "@site/docs/ww-i18n.md",
  "permalink": "/docs/ww-i18n",
  "editUrl": "https://github.com/roedoejet/wordweaver-docs/edit/master/docs/ww-i18n.md",
  "sidebar_label": "Translation & Internationalization",
  "sidebar": "someSidebar",
  "previous": {
    "title": "WordWeaver-UI Validation",
    "permalink": "/docs/ww-ui-validation"
  },
  "next": {
    "title": "WordWeaver-UI Type Definitions",
    "permalink": "/docs/ww-ui-types"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: '1. Determine your language&#39;s code',
  id: '1-determine-your-languages-code',
  children: []
}, {
  value: '2. Create your file',
  id: '2-create-your-file',
  children: []
}, {
  value: '3. Update your WordWeaver Configuration',
  id: '3-update-your-wordweaver-configuration',
  children: []
}, {
  value: '4. Extract default translations',
  id: '4-extract-default-translations',
  children: []
}, {
  value: '5. Update your translations',
  id: '5-update-your-translations',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><em parentName="p">{`This guide assumes you have followed the steps of both `}<a parentName="em" {...{
          "href": "/docs/installation"
        }}>{`installing`}</a>{` and `}<a parentName="em" {...{
          "href": "/docs/firststeps"
        }}>{`setting up`}</a>{` WordWeaver. It also assumes some familiarity with `}<a parentName="em" {...{
          "href": "https://en.wikipedia.org/wiki/JSON"
        }}>{`JSON`}</a>{`, basic `}<a parentName="em" {...{
          "href": "/docs/wwui-custom-intro#the-basic-layout"
        }}>{`directory structure`}</a>{` of your WordWeaver, `}<a parentName="em" {...{
          "href": "https://en.wikipedia.org/wiki/Command-line_interface"
        }}>{`Command Line`}</a>{`, and `}<a parentName="em" {...{
          "href": "https://en.wikipedia.org/wiki/Npm_(software)"
        }}>{`NPM`}</a></em></p>
    <p>{`The basic interface for your WordWeaver is already translated into English and French. But, you may want to add translations for your own language. This is the guide to show you how to do that.`}</p>
    <h2 {...{
      "id": "1-determine-your-languages-code"
    }}>{`1. Determine your language's code`}</h2>
    <p>{`Use a unique 2 or 3 digit code, or look yours up in the `}<a parentName="p" {...{
        "href": "https://iso639-3.sil.org/code_tables/639/data"
      }}>{`ISO 639-3 standard`}</a>{`. Make note of this, you will have to use it in the next steps.`}</p>
    <h2 {...{
      "id": "2-create-your-file"
    }}>{`2. Create your file`}</h2>
    <p>{`Add a file to your i18n assets as seen below:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text",
        "metastring": "{15}",
        "{15}": true
      }}>{`.
+-- projects
|   +-- server
|   +-- word-weaver
|       +-- src
|           +-- app
|               +-- app
|               +-- core
|               +-- pages
|               +-- shared
|           +-- assets
|               +-- i18n
|                   +-- en.json
|                   +-- fr.json
|                   +-- <yourcode>.json
|               +-- wwlogo.png // TODO: Rename this
|               +-- logo.svg // TODO: Rename this
|           +-- config
|               +-- config.fr.ts
|               +-- config.moh.ts
|               +-- config.ts
|           +-- environments
|               +-- environment.prod.ts
|               +-- environment.test.ts
|               +-- environment.ts
|           +-- scss
|           +-- themes
|           +-- index.html
|           +-- styles.scss
+-- angular.json
+-- package.json
`}</code></pre>
    <h2 {...{
      "id": "3-update-your-wordweaver-configuration"
    }}>{`3. Update your WordWeaver Configuration`}</h2>
    <p>{`Update your configuration values for `}<a parentName="p" {...{
        "href": "/docs/wwui-custom-intro#languages"
      }}>{`languages`}</a>{`. If you want to change your default language, you can `}<a parentName="p" {...{
        "href": "/docs/wwui-custom-intro#language"
      }}>{`also update that`}</a>{`.`}</p>
    <h2 {...{
      "id": "4-extract-default-translations"
    }}>{`4. Extract default translations`}</h2>
    <p>{`Use the npm script, `}<inlineCode parentName="p">{`npm run-script extract-translations`}</inlineCode>{` to update your json file.`}</p>
    <h2 {...{
      "id": "5-update-your-translations"
    }}>{`5. Update your translations`}</h2>
    <p>{`You now must change the values for each key in `}<inlineCode parentName="p">{`projects/word-weaver/src/assets/i18n/<yourcode>.json`}</inlineCode>{` to your language. We recommend using either a desktop tool like, `}<a parentName="p" {...{
        "href": "https://www.codeandweb.com/babeledit"
      }}>{`BabelEdit`}</a>{`, or an online collaborative resource like `}<a parentName="p" {...{
        "href": "https://poeditor.com/"
      }}>{`POEditor`}</a></p>
    <p>{`That's it - your website will now include your translations!`}</p>
    <div {...{
      "className": "admonition admonition-info alert alert--info"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M7 2.3c3.14 0 5.7 2.56 5.7 5.7s-2.56 5.7-5.7 5.7A5.71 5.71 0 0 1 1.3 8c0-3.14 2.56-5.7 5.7-5.7zM7 1C3.14 1 0 4.14 0 8s3.14 7 7 7 7-3.14 7-7-3.14-7-7-7zm1 3H6v5h2V4zm0 6H6v2h2v-2z"
              }}></path></svg></span>{`info`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`We make use of, and greatly appreciate, the excellent tools `}<a parentName="p" {...{
            "href": "https://github.com/ngx-translate/core"
          }}>{`ngx-translate`}</a>{` and `}<a parentName="p" {...{
            "href": "https://github.com/biesbjerg/ngx-translate-extract"
          }}>{`ngx-translate-extract`}</a>{` - yay for open source!! 🎉🎉`}</p></div></div>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
