  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'firststeps',
	title: 'Taking your first steps with WordWeaver',
	sidebar_label: 'First Steps'
};
export const metadata = {
  "id": "firststeps",
  "title": "Taking your first steps with WordWeaver",
  "description": "Going through your first steps with WordWeaver assumes you've first installed the necessary requirements.",
  "source": "@site/docs/ww-firststeps.md",
  "permalink": "/docs/firststeps",
  "editUrl": "https://github.com/roedoejet/wordweaver-docs/edit/master/docs/ww-firststeps.md",
  "sidebar_label": "First Steps",
  "sidebar": "someSidebar",
  "previous": {
    "title": "Installation",
    "permalink": "/docs/installation"
  },
  "next": {
    "title": "Configuring your WordWeaver",
    "permalink": "/docs/ww-custom-intro"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'WordWeaver',
  id: 'wordweaver',
  children: []
}, {
  value: 'WordWeaver-UI',
  id: 'wordweaver-ui',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Going through your first steps with WordWeaver assumes you've first `}<a parentName="p" {...{
        "href": "installation.md"
      }}>{`installed`}</a>{` the necessary requirements.`}</p>
    <p>{`Each WordWeaver instance will have two main parts, the main `}<em parentName="p">{`WordWeaver`}</em>{` API, and the user interface, `}<em parentName="p">{`WordWeaver-UI`}</em>{`. Each guide will make note of which part it is talking about.`}</p>
    <h3 {...{
      "id": "wordweaver"
    }}>{`WordWeaver`}</h3>
    <p>{`In order to run WordWeaver using the stock data, first `}<inlineCode parentName="p">{`cd wordweaver`}</inlineCode>{` and then run `}<inlineCode parentName="p">{`docker-compose up`}</inlineCode>{`. This will start a docker container with the WordWeaver API backend, and also a container with a CouchDB database. It will initialize the database with all of the data from `}<inlineCode parentName="p">{`wordweaver/wordweaver/wordweaver/data/fr`}</inlineCode>{`.`}</p>
    <p>{`To customize your WordWeaver, please follow the instructions `}<a parentName="p" {...{
        "href": "/docs/ww-custom-intro"
      }}>{`on configuring your WordWeaver`}</a>{`.`}</p>
    <h3 {...{
      "id": "wordweaver-ui"
    }}>{`WordWeaver-UI`}</h3>
    <p>{`Then, you should be able to go to `}<inlineCode parentName="p">{`localhost/docs`}</inlineCode>{` in your browser and look at the documentation for your WordWeaver API.`}</p>
    <p>{`In order to run the WordWeaver-UI, first `}<inlineCode parentName="p">{`cd wordweaver-UI`}</inlineCode>{` and then run `}<inlineCode parentName="p">{`npm start`}</inlineCode>{`. This will build the site using the development configuration. You should now be able to go to `}<inlineCode parentName="p">{`localhost:4200`}</inlineCode>{` and view your instance of WordWeaver.`}</p>
    <p>{`To customize your WordWeaver-UI, please follow the instructions `}<a parentName="p" {...{
        "href": "/docs/wwui-custom-intro"
      }}>{`on configuring your WordWeaver-UI`}</a></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
