  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'ww-ui-style',
	title: 'Styling your WordWeaver',
	sidebar_label: 'Styling'
};
export const metadata = {
  "id": "ww-ui-style",
  "title": "Styling your WordWeaver",
  "description": "This guide assumes you have followed the steps of both installing and setting up WordWeaver. It also assumes some familiarity with Sass",
  "source": "@site/docs/ww-ui-style.md",
  "permalink": "/docs/ww-ui-style",
  "editUrl": "https://github.com/roedoejet/wordweaver-docs/edit/master/docs/ww-ui-style.md",
  "sidebar_label": "Styling",
  "sidebar": "someSidebar",
  "previous": {
    "title": "Gramble & Language Modelling",
    "permalink": "/docs/gramble-intro"
  },
  "next": {
    "title": "WordWeaver-UI Validation",
    "permalink": "/docs/ww-ui-validation"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Constraining available themes',
  id: 'constraining-available-themes',
  children: []
}, {
  value: 'Default themes',
  id: 'default-themes',
  children: []
}, {
  value: 'Creating a new theme',
  id: 'creating-a-new-theme',
  children: [{
    value: 'Custom Palettes',
    id: 'custom-palettes',
    children: []
  }, {
    value: 'Custom Themes',
    id: 'custom-themes',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><em parentName="p">{`This guide assumes you have followed the steps of both `}<a parentName="em" {...{
          "href": "/docs/installation"
        }}>{`installing`}</a>{` and `}<a parentName="em" {...{
          "href": "/docs/firststeps"
        }}>{`setting up`}</a>{` WordWeaver. It also assumes some familiarity with `}<a parentName="em" {...{
          "href": "https://sass-lang.com/"
        }}>{`Sass`}</a></em></p>
    <p>{`WordWeaver comes with quite a few preset themes out of the box. The default available themes (seen below) are defined in your `}<a parentName="p" {...{
        "href": "/docs/wwui-custom-intro"
      }}>{`WordWeaver UI configuration`}</a>{` (`}<inlineCode parentName="p">{`projects/word-weaver/src/config/config.ts`}</inlineCode>{`)`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-typescript"
      }}>{`export const THEMES: Theme[] = [
  {
    value: "DEFAULT-THEME",
    label: marker("ww.settings.themes.blue")
  },
  {
    value: "LIGHT-THEME",
    label: marker("ww.settings.themes.light")
  },
  {
    value: "DARK-THEME",
    label: marker("ww.settings.themes.dark")
  },
  {
    value: "PURPLE-THEME--LIGHT",
    label: marker("ww.settings.themes.purple-light")
  },
  {
    value: "PURPLE-THEME--DARK",
    label: marker("ww.settings.themes.purple-dark")
  },
  {
    value: "PURPLE-BLUE-THEME--LIGHT",
    label: marker("ww.settings.themes.purple-blue-light")
  },
  {
    value: "PURPLE-BLUE-THEME--DARK",
    label: marker("ww.settings.themes.purple-blue-dark")
  },
  {
    value: "PURPLE-GOLD-THEME--LIGHT",
    label: marker("ww.settings.themes.purple-gold-light")
  },
  {
    value: "PURPLE-GOLD-THEME--DARK",
    label: marker("ww.settings.themes.purple-gold-dark")
  }
];
`}</code></pre>
    <h2 {...{
      "id": "constraining-available-themes"
    }}>{`Constraining available themes`}</h2>
    <p>{`To constrain the list of available themes from your users, just remove them from your configuration.`}</p>
    <h2 {...{
      "id": "default-themes"
    }}>{`Default themes`}</h2>
    <p>{`By following our guides on configuring your WordWeaver, you can `}{`[constrain what themes are available to your users]`}{`, and also set the `}<a parentName="p" {...{
        "href": "/docs/wwui-custom-intro#theme"
      }}>{`default theme`}</a>{` and default night theme.`}</p>
    <h2 {...{
      "id": "creating-a-new-theme"
    }}>{`Creating a new theme`}</h2>
    <p>{`Themes are built around matching two basic colours: a `}<inlineCode parentName="p">{`$primary`}</inlineCode>{` colour and an `}<inlineCode parentName="p">{`$accent`}</inlineCode>{`.
These colours are actually `}<a parentName="p" {...{
        "href": "https://material.io/design/color/the-color-system.html#color-usage-and-palettes"
      }}>{`material design colour palettes`}</a>{`. `}</p>
    <h3 {...{
      "id": "custom-palettes"
    }}>{`Custom Palettes`}</h3>
    <p>{`You can either use Angular Material's built-in palettes, or you can make your own. Making your own requires creating an scss variable map for your colour palette.
You can either do that manually or provide a single colour and use a generator. The generator we recommend is `}<a parentName="p" {...{
        "href": "http://mcg.mbitson.com/"
      }}>{`this one`}</a>{`.
You just select the colour and download the code with output format `}<inlineCode parentName="p">{`ANGULAR JS 2 (MATERIAL 2)`}</inlineCode>{`.
Then, your palette can be added to `}<inlineCode parentName="p">{`projects/word-weaver/src/themes/palettes.scss`}</inlineCode>{`. `}</p>
    <div {...{
      "className": "admonition admonition-note alert alert--secondary"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
              }}></path></svg></span>{`note`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`Make sure your variable for your scss map is not already defined`}</p></div></div>
    <h3 {...{
      "id": "custom-themes"
    }}>{`Custom Themes`}</h3>
    <p>{`To create a new theme:`}</p>
    <ol>
      <li parentName="ol">{`Create a new theme file in `}<inlineCode parentName="li">{`projects/word-weaver/src/themes/`}</inlineCode></li>
      <li parentName="ol">{`Define your `}<inlineCode parentName="li">{`primary`}</inlineCode>{`, `}<inlineCode parentName="li">{`accent`}</inlineCode>{`, and `}<inlineCode parentName="li">{`warn`}</inlineCode>{` palettes. You can use material palettes (examples `}<inlineCode parentName="li">{`$mat-deep-purple`}</inlineCode>{` and `}<inlineCode parentName="li">{`mat-amber`}</inlineCode>{` below) or your own custom ones (example `}<inlineCode parentName="li">{`$my-custom-palette`}</inlineCode>{` below):`}</li>
    </ol>
    <pre><code parentName="pre" {...{
        "className": "language-scss"
      }}>{`@import "palettes.scss";

$ww-example-theme-primary: mat-palette($mat-deep-purple, 400);
$ww-example-theme-accent: mat-palette($mat-amber, 600);
$ww-example-theme-warn: mat-palette($my-custom-palette, 200);

`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Then, use the `}<inlineCode parentName="li">{`mat-light-theme`}</inlineCode>{` and `}<inlineCode parentName="li">{`mat-dark-theme`}</inlineCode>{` wrapper functions and define your themes.`}</li>
    </ol>
    <pre><code parentName="pre" {...{
        "className": "language-scss"
      }}>{`$ww-example-theme--light: mat-light-theme(
  $ww-example-theme-primary,
  $ww-example-theme-accent,
  $ww-example-theme-warn
);

$ww-example-theme--dark: mat-dark-theme(
  $ww-example-theme-primary,
  $ww-example-theme-accent,
  $ww-example-theme-warn
);
`}</code></pre>
    <ol {...{
      "start": 4
    }}>
      <li parentName="ol">{`Add your theme to the main style sheet by adding it to `}<inlineCode parentName="li">{`projects/word-weaver/src/styles.scss`}</inlineCode></li>
    </ol>
    <pre><code parentName="pre" {...{
        "className": "language-scss",
        "metastring": "{7-11}",
        "{7-11}": true
      }}>{`.purple-gold-theme--dark {
  @include angular-material-theme($ww-purple-gold-theme--dark);
  @include custom-components-theme-common($ww-purple-gold-theme--dark);
  @include custom-components-theme-dark($ww-purple-gold-theme--dark);
}

.example-theme--dark {
  @include angular-material-theme($ww-example-theme--dark);
  @include custom-components-theme-common($ww-example-theme--dark);
  @include custom-components-theme-dark($ww-example-theme--dark);
}
`}</code></pre>
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol">{`Add your theme to the `}<inlineCode parentName="li">{`THEMES`}</inlineCode>{` variable in `}<a parentName="li" {...{
          "href": "/docs/wwui-custom-intro"
        }}>{`your configuration`}</a>{`.`}</li>
    </ol>
    <pre><code parentName="pre" {...{
        "className": "language-typescript",
        "metastring": "{38-45}",
        "{38-45}": true
      }}>{`export const THEMES: Theme[] = [
  {
    value: "DEFAULT-THEME",
    label: marker("ww.settings.themes.blue")
  },
  {
    value: "LIGHT-THEME",
    label: marker("ww.settings.themes.light")
  },
  {
    value: "DARK-THEME",
    label: marker("ww.settings.themes.dark")
  },
  {
    value: "PURPLE-THEME--LIGHT",
    label: marker("ww.settings.themes.purple-light")
  },
  {
    value: "PURPLE-THEME--DARK",
    label: marker("ww.settings.themes.purple-dark")
  },
  {
    value: "PURPLE-BLUE-THEME--LIGHT",
    label: marker("ww.settings.themes.purple-blue-light")
  },
  {
    value: "PURPLE-BLUE-THEME--DARK",
    label: marker("ww.settings.themes.purple-blue-dark")
  },
  {
    value: "PURPLE-GOLD-THEME--LIGHT",
    label: marker("ww.settings.themes.purple-gold-light")
  },
  {
    value: "PURPLE-GOLD-THEME--DARK",
    label: marker("ww.settings.themes.purple-gold-dark")
  },
  {
    value: "EXAMPLE-THEME--LIGHT",
    label: marker("ww.settings.themes.example-light")
  },
  {
    value: "EXAMPLE-THEME--DARK",
    label: marker("ww.settings.themes.example-dark")
  }
];
`}</code></pre>
    <p>{`That's it! You've added your new theme. Congratulations!`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
